syntax = "proto3";

package Net.Fabrikam.Telemetry;

message Channel {
  ChannelTypeEnum ChannelType = 1;
  optional string CarId = 2;
  optional string SessionId = 3;
  optional string LapId = 4;
  int64 SampleCount = 5;
  int64 Frequency = 6;
  BatchTimespanType BatchTimespan = 7;
  repeated double Data = 8;
  enum ChannelTypeEnum {
    EngineMaxRpm = 0;
    EngineIdleRpm = 1;
    CurrentEngineRpm = 2;
    AccelerationX = 3;
    AccelerationY = 4;
    AccelerationZ = 5;
    VelocityX = 6;
    VelocityY = 7;
    VelocityZ = 8;
    AngularVelocityX = 9;
    AngularVelocityY = 10;
    AngularVelocityZ = 11;
    Yaw = 12;
    Pitch = 13;
    Roll = 14;
    NormalizedSuspensionTravelFrontLeft = 15;
    NormalizedSuspensionTravelFrontRight = 16;
    NormalizedSuspensionTravelRearLeft = 17;
    NormalizedSuspensionTravelRearRight = 18;
    TireSlipRatioFrontLeft = 19;
    TireSlipRatioFrontRight = 20;
    TireSlipRatioRearLeft = 21;
    TireSlipRatioRearRight = 22;
    WheelRotationSpeedFrontLeft = 23;
    WheelRotationSpeedFrontRight = 24;
    WheelRotationSpeedRearLeft = 25;
    WheelRotationSpeedRearRight = 26;
    WheelOnRumbleStripFrontLeft = 27;
    WheelOnRumbleStripFrontRight = 28;
    WheelOnRumbleStripRearLeft = 29;
    WheelOnRumbleStripRearRight = 30;
    WheelInPuddleDepthFrontLeft = 31;
    WheelInPuddleDepthFrontRight = 32;
    WheelInPuddleDepthRearLeft = 33;
    WheelInPuddleDepthRearRight = 34;
    SurfaceRumbleFrontLeft = 35;
    SurfaceRumbleFrontRight = 36;
    SurfaceRumbleRearLeft = 37;
    SurfaceRumbleRearRight = 38;
    TireSlipAngleFrontLeft = 39;
    TireSlipAngleFrontRight = 40;
    TireSlipAngleRearLeft = 41;
    TireSlipAngleRearRight = 42;
    TireCombinedSlipFrontLeft = 43;
    TireCombinedSlipFrontRight = 44;
    TireCombinedSlipRearLeft = 45;
    TireCombinedSlipRearRight = 46;
    SuspensionTravelMetersFrontLeft = 47;
    SuspensionTravelMetersFrontRight = 48;
    SuspensionTravelMetersRearLeft = 49;
    SuspensionTravelMetersRearRight = 50;
  }
  message BatchTimespanType {
    int64 StartTS = 1;
    int64 EndTS = 2;
  }
}
